) ENGINE = InnoDB AUTO_INCREMENT = 1900196815346221059 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '新闻标签' ROW_FORMAT = DYNAMIC;

