INSERT INTO `news` VALUES (1920889012269854722, '诞生于243234', '<p style=\"text-indent: 2em; text-align: justify;\"><br></p><p style=\"text-align: start;\">　欢迎仪式，小范围、大范围会谈，签字和合作文本交换仪式，共见记者，欢迎宴会，茶叙……习近平主席对俄罗斯进行国事访问，5月8日这一天，中俄两国元首之间的亲切互动和深入交流，从上午10点一直持续到傍晚。</p><p style=\"text-align: start;\">　　习近平主席在同普</p>', NULL, 'aw', NULL, 'admin', 1903048402666127361, 0, 2, 0, 0, NULL, '2025-05-10 01:10:03', '2025-05-10 13:40:45', 0);
